    var buzzer = e;
    if (e < 0 && intervalID && intervalID.cancle()) {
      if (timerType === "Session") {
        startTime();
        swithTimer(60 * bV , "Break");
      } else {
        startTime();
        swithTimer(60 * sV, "Session");
      }
    }
  }

  function swithTimer(t, str) {
    setTimer(t);
    setTimTy(str);
  }

  function buzzer(e) {
    if (e === 0 ) {
      audioBeep.play();
    }
  }

  function warning(e) {
    if (e < 61) {
      setAlarmCol({
        color: '#a50d0d'
      })
    } else {
      setAlarmCol({
        color: 'white'
      })
    }
  }

  function clockify() {
    if (timer < 0) {
      return '00:00';
    } else {
      var e = Math.floor(timer / 60),
          t = timer - 60 * e;
      return (e = e < 10 ? "0" + e : e) + ":" + (t = t < 10 ? "0" + t : t)
    }
  }

