import { useState } from 'react';
import './App.css';

function App() {
  const [status, setStatus] = useState('stopped');
  const [bV, setBv] = useState(5);
  const [sV, setSv] = useState(1);
  const [timerType, setTimTy] = useState('Session');
  const [timer, setTimer] = useState(sV*60);
  const [intervalID, setIntID] = useState("");
  const [alarmColor, setAlarmCol] = useState({
    color: 'white'
  })
  const audioBeep = document.getElementById('beep');

  function setBrkLen(e) {
    lengthControl(e.currentTarget.value, bV, "Session");
  }

  function setSesLen(e) {
    lengthControl(e.currentTarget.value, sV, "Break");
  }

  function lengthControl(val, leng, str) {
    if (status !== 'running') {
      if (timerType === str) {
        if (val === '-' && leng !== 1) {
          setBv(bV-1)
        } else if (val === '+' && leng !== 60) {
          setBv(bV+1)
        }
      } else if (val === '-' && leng !==1) {
        let a = sV - 1;
        setSv(a);
        setTimer(a*60)
      } else if (val === '+' && leng !== 60) {
        let a = sV + 1;
        setSv(a);
        setTimer(a*60);
      }
    }
  }

  async function timerControl() {
    if (status === 'stopped') {
      console.log(status)
      setStatus('running');
    } else {
      console.log(status)
      setStatus('stopped')
    }
    // if (stat !== 'stopped') {
    //   beginCountdown();
    //   setStatus('stopped');
    //   console.log("stopped", stat);
    // } else {
    //   setStatus('running');
    //   console.log('running', status);
    // }
    // intervalID && intervalID.cancle();
  }

  function beginCountdown() {
    var e,
        t,
        n,
        r,
        i,
        a,
        o;
    decrementTimer();
    phaseControl();
    t = 1e3;
    r = new Date().getTime() + t;
    i = null;
    a = () => {
      return r += t, i = setTimeout(a, r - new Date().getTime(), e)
    }
    n = () => {
      return clearTimeout(i)
    }
    i = () => {
      setTimeout(a, r - new Date().getTime())
    }
    n = 'cancel'
  }

  function decrementTimer() {
    let a = timer - 1;
    setTimer(a);
  }

  function phaseControl() {
    var e = timer;
    warning(e);
    buzzer(e);
    if (e < 0) {
      if (timerType !== "Session") {
        beginCountdown();
        swithTimer(60 * bV , "Break");
      } else {
        beginCountdown();
        swithTimer(60 * sV, "Session");
      }
    }
  }

  function warning(e) {
    if (e < 61) {
      setAlarmCol({
        color: '#a50d0d'
      })
    } else {
      setAlarmCol({
        color: 'white'
      })
    }
  }

  function buzzer(e) {
    if ( e === 0) {
      audioBeep.play();
    }
  }

  function swithTimer(t, str) {
    setTimer(t);
    setTimTy(str);
    setAlarmCol({
      color: 'white'
    })
  }

  function clockify() {
    if (timer === 0) {
      return '00:00';
    } else {
      var e = Math.floor(timer / 60),
          t = timer - 60 * e;
      return (e = e < 10 ? "0" + e : e) + ":" + (t = t < 10 ? "0" + t : t)
    }
  }

  function resetTime() {
    setBv(5);
    setSv(25);
    setStatus('Stopped');
    setTimTy('Session');
    setTimer(1500);
    setIntID("");
    setAlarmCol({
      color: 'white'
    });
    // intervalID && intervalID.cancle();
    audioBeep.pause();
    audioBeep.currentTime= 0;
  }

  return (
    <div className="App">
      <div id='container'>
        <div>
          <div className='main-title'>25 + 5 Clock</div>
          <div className='length-control'>
            <div id='break-label'>Break Length</div>
            <button id='break-decrement' className='btn-label' value={'-'} onClick={(e) => setBrkLen(e)}>
              <i className='fa fa-arrow-down fa-2x'></i>
            </button>
            <div id='break-length' className='btn-label'>{bV}</div>
            <button id='break-increament' className='btn-label' value={'+'} onClick={(e) => setBrkLen(e)}>
              <i className='fa fa-arrow-up fa-2x'></i>
            </button>
          </div>
          <div className='length-control'>
            <div id='session-label'>Session Length</div>
            <button id='session-decrement' className='btn-label'  value={"-"} onClick={(e) => setSesLen(e)}>
              <i className='fa fa-arrow-down fa-2x'></i>
            </button>
            <div id='session-length' className='btn-label'>{sV}</div>
            <button id='session-increment' className='btn-label' value={'+'} onClick={(e) => setSesLen(e)}>
              <i className='fa fa-arrow-up fa-2x'></i>
            </button>
          </div>
          <div className='timer' style={alarmColor}>
            <div className='timer-wrapper'>
              <div id='timer-label'>{timerType}</div>
              <div id='time-left'>{clockify()}</div>
            </div>
          </div>
          <div className='timer-control'>
            <button id='start_stop' onClick={timerControl}>
              <i className='fa fa-play fa-2x'></i>
              <i className='fa fa-pause fa-2x'></i>
            </button>
            <button id='reset' onClick={resetTime}>
              <i className='fa fa-refresh fa-2x'></i>
            </button>
          </div>
          <div className='author'>
            "Designed and Coded by "
            <br/>
            <a href='https://www.freecodecamp.org/showansimkhada' target='_blank' rel="noopener noreferrer">Showan Simkhada</a>
          </div>
          <audio id='beep' src='https://raw.githubusercontent.com/freeCodeCamp/cdn/master/build/testable-projects-fcc/audio/BeepSound.wav'></audio>
        </div>
      </div>
    </div>
  );
}

export default App;
