import { useState } from 'react';
import './App.css';

function App() {
  const [bV, setBv] = useState(5);
  const [sV, setSv] = useState(25);
  const [min, setMin] = useState(25);
  const [sec, setSec] = useState('00')


  return (
    <div className="App">
      <div id='container'>
        <div>
          <div className='main-title'>25 + 5 Clock</div>
          <div className='length-control'>
            <div id='break-label'>Break Length</div>
            <button id='break-decrement' className='btn-label' value={'-'}>
              <i className='fa fa-arrow-down fa-2x' onClick={() => {
                let a = bV;
                a--;
                setBv(a);
              }}></i>
            </button>
            <div id='break-length' className='btn-label'>{bV}</div>
            <button id='break-increament' className='btn-label' value={'+'}>
              <i className='fa fa-arrow-up fa-2x' onClick={() => {
                let a = bV;
                a++;
                setBv(a);
              }}></i>
            </button>
          </div>
          <div className='length-control'>
            <div id='session-label'>Session Length</div>
            <button id='session-decrement' className='btn-label' value={'-'}>
              <i className='fa fa-arrow-down fa-2x' onClick={() => {
                let a = sV;
                a--;
                setSv(a);
                setMin(a);
              }}></i>
            </button>
            <div id='session-length' className='btn-label'>{sV}</div>
            <button id='session-increment' className='btn-label' value={'+'}>
              <i className='fa fa-arrow-up fa-2x' onClick={() => {
                let a = sV;
                a++;
                setSv(a);
                setMin(a);
              }}></i>
            </button>
          </div>
          <div className='timer' style={{color: 'white'}}>
            <div className='timer-wrapper'>
              <div id='timer-label'>Session</div>
              <div id='time-left'>{min + ":" + sec}</div>
            </div>
          </div>
          <div className='timer-control'>
            <button id='start_stop' onClick={() => {
              startTime();
            }}>
              <i className='fa fa-play fa-2x'></i>
              <i className='fa fa-pause fa-2x'></i>
            </button>
            <button id='reset' onClick={() => {
              resetTime();
            }}>
              <i className='fa fa-refresh fa-2x'></i>
            </button>
          </div>
          <div className='author'>
            "Designed and Coded by "
            <br/>
            <a href='https://www.freecodecamp.org/showansimkhada' target='_blank' rel="noopener noreferrer">Showan Simkhada</a>
          </div>
          <audio id='beep' preload='auto' src='https://raw.githubusercontent.com/freeCodeCamp/cdn/master/build/testable-projects-fcc/audio/BeepSound.wav'></audio>
        </div>
      </div>
    </div>
  );
}

export default App;
