import { useState } from 'react';
import './App.css';

function App() {
  const [status, setStatus] = useState('stopped');
  const [bV, setBv] = useState(5);
  const [sV, setSv] = useState(1);
  const [min, setMin] = useState(sV);
  const [sec, setSec] = useState('00');
  const [timeLeft, setTL] = useState('');
  const [timer, setTimer] = useState(1500);
  const [timerState, setTimStat] = useState('stopped');
  const [intervalID, setIntID] = useState("")

  function clockify() {
    if (timer < 0) {
      return '00:00';
    } else {
      var e = Math.floor(timer / 60),
          t = timer - 60 * e;
      return (e = e < 10 ? "0" + e : e) + ":" + (t = t < 10 ? "0" + t : t)
    }
  }

  // function timerControl() {
  //   if ('stopped' === timerState ? (startTime(), 
  //     timerState = 'running' ) : (
  //       timerState = 'stopped'
  //     ) 
  // }

  async function startTime() {
    setStatus(!status);
    if (status) {
      let leftSec = 59;
      let leftMin = sV-1;
      while (leftMin >= 0) {
        setMin(leftMin);
        while (leftSec >= 0) {
          if (leftSec === 0 && leftSec < 10) {
            setSec('0' + leftSec.toString());
            await new Promise(resolve => setTimeout(resolve, 1000));
            if (min === 0) {
              playAlarm();
            }
            leftSec--;
          } else {
            setSec(leftSec.toString());
            await new Promise(resolve => setTimeout(resolve, 1000));
            leftSec--;
          }      
        }
        leftMin--;
      }
    } else {
      console.log(min);
      console.log(sec);
    }
  }

  function resetTime() {
    setBv(5);
    setSv(25);
    setMin(25);
    setSec('00');
  }

  function playAlarm() {
    const sound = document.getElementById('beep');
    sound.currentTime = 0;
    sound.play();
  }

  return (
    <div className="App">
      <div id='container'>
        <div>
          <div className='main-title'>25 + 5 Clock</div>
          <div className='length-control'>
            <div id='break-label'>Break Length</div>
            <button id='break-decrement' className='btn-label' value={'-'}>
              <i className='fa fa-arrow-down fa-2x' onClick={() => {
                let a = bV;
                a--;
                if (a > 0) {
                  setBv(a);
                }
              }}></i>
            </button>
            <div id='break-length' className='btn-label'>{bV}</div>
            <button id='break-increament' className='btn-label' value={'+'}>
              <i className='fa fa-arrow-up fa-2x' onClick={() => {
                let a = bV;
                a++;
                if (a <= 60) {
                  setBv(a);
                }
              }}></i>
            </button>
          </div>
          <div className='length-control'>
            <div id='session-label'>Session Length</div>
            <button id='session-decrement' className='btn-label'>
              <i className='fa fa-arrow-down fa-2x' onClick={() => {
                let a = sV;
                a--;
                if (a > 0) {
                  setSv(a);
                  setMin(a);
                }
              }}></i>
            </button>
            <div id='session-length' className='btn-label'>{sV}</div>
            <button id='session-increment' className='btn-label'>
              <i className='fa fa-arrow-up fa-2x' onClick={() => {
                let a = sV;
                a++;
                if (a <= 60) {
                  setSv(a);
                  setMin(a);
                }
              }}></i>
            </button>
          </div>
          <div className='timer' style={{color: 'white'}}>
            <div className='timer-wrapper'>
              <div id='timer-label'>Session</div>
              <div id='time-left'>{min + ":" + sec}</div>
            </div>
          </div>
          <div className='timer-control'>
            <button id='start_stop' onClick={startTime}>
              <i className='fa fa-play fa-2x'></i>
              <i className='fa fa-pause fa-2x'></i>
            </button>
            <button id='reset' onClick={resetTime}>
              <i className='fa fa-refresh fa-2x'></i>
            </button>
          </div>
          <div className='author'>
            "Designed and Coded by "
            <br/>
            <a href='https://www.freecodecamp.org/showansimkhada' target='_blank' rel="noopener noreferrer">Showan Simkhada</a>
          </div>
          <audio id='beep' src='https://raw.githubusercontent.com/freeCodeCamp/cdn/master/build/testable-projects-fcc/audio/BeepSound.wav'></audio>
        </div>
      </div>
    </div>
  );
}

export default App;
